import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './DynamicPricingDashboard.css';
import {
  ResponsiveContainer,
  LineChart,
  Line,
  CartesianGrid,
  XAxis,
  YAxis,
  Tooltip,
  Legend
} from 'recharts';

const DynamicPricingDashboard = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState('');
  const [demandScore, setDemandScore] = useState(0);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [currentStock, setCurrentStock] = useState(0);
  const [currentPrice, setCurrentPrice] = useState(0);
  const [daysLeft, setDaysLeft] = useState(7);
  const [forecastResults, setForecastResults] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [chartData, setChartData] = useState([]);
  const [dashboardStats, setDashboardStats] = useState({
    totalRecommendations: 0,
    averagePriceChange: 0,
    averageConfidence: 0,
    uniqueProducts: 0
  });

  // Fetch overview data on component mount
  useEffect(() => {

  const fetchOverviewData = async () => {
    try {
      const overviewResponse = await axios.get('/api/dashboard/overview');
      if (overviewResponse.data.success && overviewResponse.data.data) {
        const data = overviewResponse.data.data;
        setDashboardStats({
          total_recommendations: data.dynamic_pricing.total_recommendations || 0,
          average_price_change: data.dynamic_pricing.average_price_change || 0,
          average_confidence: data.dynamic_pricing.average_confidence || 0,
          unique_products: data.dynamic_pricing.unique_products || 0
        });
      }
    } catch (error) {
      console.error('Error fetching overview data:', error);
      setError('Failed to fetch overview data');
    }
     try {
          // 1. Fetch products
          const productsResponse = await axios.get('/api/products');
          console.log('Products response:', productsResponse.data);

          if (productsResponse.data.success && productsResponse.data.data) {
            setProducts(productsResponse.data.data);
            console.log('Products loaded:', productsResponse.data.data.length);
          }

          // 2. Fetch categories
          const categoriesResponse = await axios.get('/api/products/meta/categories');
          if (categoriesResponse.data.success && Array.isArray(categoriesResponse.data.data)) {
            setCategories(categoriesResponse.data.data);
            console.log('Categories:', categoriesResponse.data.data);
          }
        } catch (error) {
          console.error('Failed to fetch data from backend:', error);
        }

  };
  

  const fetchAnalyticsData = async () => {
    try {
      const response = await axios.get('/api/pricing/analytics');
      // Ensure analytics data is always an array
      const data = response.data;
      if (Array.isArray(data)) {
        setAnalyticsData(data);
      } else if (data && Array.isArray(data.analytics)) {
        // Handle case where data is wrapped in an object
        setAnalyticsData(data.analytics);
      } else {
        console.warn('Analytics API response is not an array:', data);
        setAnalyticsData([]);
      }
    } catch (error) {
      console.error('Error fetching analytics data:', error);
      setError('Failed to fetch analytics data');
      setAnalyticsData([]); // Ensure empty array on error
    }
  };

  const fetchOptimizationSummary = async () => {
    try {
      const optimizationresponse = await axios.get('/api/pricing/optimization-summary');
      setOptimizationSummary(optimizationresponse.data || {});
    } catch (error) {
      console.error('Error fetching optimization summary:', error);
      setError('Failed to fetch optimization summary');
    }
  };
    fetchOverviewData();
    fetchAnalyticsData();
    fetchOptimizationSummary();
   }, []);

  const handleGenerateForecast = async () => {
    if (!selectedProduct || !demandScore || !currentStock || !daysLeft || !currentPrice) {
      setError('Please fill all required fields');
      return;
    }

    setLoading(true);
    setError('');
    
    try {
      const selectedProductData = products.find(p => p.product_id === selectedProduct);
      
      const requestBody = {
        products: [{ product_name: selectedProductData?.product_name || selectedProduct }],
        current_price: parseInt(currentPrice),
        stock_level: parseInt(currentStock),
        days_left: parseInt(daysLeft),
        demand_score: parseInt(demandScore)

      };
      
      console.log('Sending forecast request:', requestBody);
      
      const response = await axios.post('/api/pricing/recommend', requestBody);
      
      console.log('Forecast response:', response.data);
      setForecastResults(response.data.data);
      
      // Update dashboard stats
      setDashboardStats({
      totalRecommendations: response.data.data.total_recommendations || 1,
      avgPriceChange: response.data.data.avg_price_change || 63.35,
      avgConfidence: Math.round((response.data.data.avg_confidence || 0.8) * 100),
      uniqueProducts: response.data.data.unique_products || 1
    });
      
    } catch (error) {
      console.error('Error generating forecast:', error);
      setError(`Failed to generate forecast: ${error.response?.data?.message || error.message}`);
    }
    
    setLoading(false);
  };

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <button className="back-btn" onClick={onNavigateHome}>
          ← Back to Home
        </button>
        <h1 className="dashboard-title">Dynamic Pricing Dashboard</h1>
      </div>
      
      {error && <div className="error-message">{error}</div>}
      
      {/* Stats Cards */}
      <div className="stats-grid">
        <div className="stat-card">
          <h3>Total Recommendations</h3>
          <p className="stat-value">{dashboardStats.total_recommendations}</p>
        </div>
        <div className="stat-card">
          <h3>Average Price Change</h3>
          <p className="stat-value">{dashboardStats.average_price_change.toFixed(2)}%</p>
        </div>
        <div className="stat-card">
          <h3>Average Confidence</h3>
          <p className="stat-value">{dashboardStats.average_confidence.toFixed(2)}%</p>
        </div>
        <div className="stat-card">
          <h3>Unique Products</h3>
          <p className="stat-value">{dashboardStats.unique_products}</p>
        </div>
      </div>

      {/* Main Content Grid */}
      <div className="main-content">
        {/* Analytics Chart */}
        <div className="chart-section">
          <h2>Price Analytics</h2>
          <div className="chart-container">
            <ResponsiveContainer width="100%" height={400}>
              <LineChart data={analyticsData}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="category" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Line 
                  type="monotone" 
                  dataKey="average_current_price" 
                  stroke="#8884d8" 
                  strokeWidth={2}
                  name="Average Current Price"
                />
                <Line 
                  type="monotone" 
                  dataKey="average_recommended_price" 
                  stroke="#82ca9d" 
                  strokeWidth={2}
                  name="Average Recommended Price"
                />
              </LineChart>
            </ResponsiveContainer>
          </div>
        </div>

      {/* Forecast Card Section - 40% width */}
        <div className="forecast-card-section">
          <div className="generate-forecast-card">
          <h3>Generate new Forecast</h3>
          
          <div className="control-group">
            <label>Select Product</label>
            <select 
              value={selectedProduct} 
              onChange={e => setSelectedProduct(e.target.value)}
              className="control-select"
            >
              <option value="">Choose a product...</option>
              {products.map(product => (
                <option key={product.product_id} value={product.product_id}>
                  {product.product_name}
                </option>
              ))}
            </select>
          </div>

          <div className="control-group">
            <label>Select City</label>
            <select 
              value={selectedCity} 
              onChange={e => setSelectedCity(e.target.value)}
              className="control-select"
            >
              <option value="">Choose a city...</option>
              {cities.map(city => (
                <option key={city} value={city}>{city}</option>
              ))}
            </select>
          </div>
          
          <div className="control-group">
          <label>Select Category</label>
          <select 
            value={selectedCategory} 
            onChange={async (e) => {
              const category = e.target.value;
              setSelectedCategory(category);
              setSelectedProduct(''); // Reset selected product

              if (category === '') {
                // If "All categories..." selected, fetch all products
                try {
                  const res = await axios.get('/api/products');
                  if (res.data.success && Array.isArray(res.data.data)) {
                    setProducts(res.data.data);
                  }
                } catch (err) {
                  console.error('Error fetching all products:', err);
                }
              } else {
                // Fetch products by selected category
                try {
                  const res = await axios.get(`/api/products/category/${category}`);
                  if (res.data.success && Array.isArray(res.data.data)) {
                    setProducts(res.data.data);
                  }
                } catch (err) {
                  console.error('Error fetching category products:', err);
                }
              }
            }}
            className="control-select"
          >
            <option value="">All categories...</option>
            {categories.map(category => (
              <option key={category} value={category}>{category}</option>
            ))}
          </select>
        </div>

          
         <div className="control-group">
        <label>Current Stock</label>
        <input
          type="text"
          value={currentStock}
          onChange={(e) => {
            const val = e.target.value;
            if (/^\d*$/.test(val)) setCurrentStock(val); // allow only digits
          }}
          placeholder="Enter current stock"
          className="control-input"
        />
      </div>

      <div className="control-group">
        <label>Days Left</label>
        <input
          type="text"
          value={daysLeft}
          onChange={(e) => {
            const val = e.target.value;
            if (/^\d*$/.test(val)) setDaysLeft(val); // allow only digits
          }}
          placeholder="Number of days"
          className="control-input"
        />
      </div>
      <div className="control-group">
        <label>Current Price</label>
        <input
          type="text"
          value={currentPrice}
          onChange={(e) => {
            const val = e.target.value;
            if (/^\d*$/.test(val)) setCurrentPrice(val); // allow only digits
          }}
          placeholder="Price"
          className="control-input"
        />
      </div>
      <div className="control-group">
        <label>Demand Score</label>
        <input
          type="text"
          value={demandScore}
          onChange={(e) => {
            const val = e.target.value;
            if (/^\d*$/.test(val)) setDemandScore(val); // allow only digits
          }}
          placeholder="Number of days"
          className="control-input"
        />
      </div>

          
          <button 
            onClick={handleGenerateForecast}
            disabled={loading}
            className="generate-card-btn"
          >
            {loading ? '⏳ Generating...' : ' Generate'}
          </button>
          
          {error && <div className="error-message" style={{marginTop: '10px', padding: '10px', fontSize: '0.8rem'}}>{error}</div>}
        </div>
        </div>
      </div>

      {/* Recommendations Results */}
      {recommendations.length > 0 && (
        <div className="results-section">
          <h2>Pricing Recommendations</h2>
          <div className="recommendations-grid">
            {recommendations.map((rec, index) => (
              <div key={index} className="recommendation-card">
                <h3>{rec.product_name}</h3>
                <div className="price-info">
                  <div className="price-item">
                    <span className="label">Current Price:</span>
                    <span className="value">₹{rec.current_price}</span>
                  </div>
                  <div className="price-item">
                    <span className="label">Recommended Price:</span>
                    <span className="value recommended">₹{rec.recommended_price}</span>
                  </div>
                  <div className="price-item">
                    <span className="label">Days Left:</span>
                    <span className="value">{rec.days_left}</span>
                  </div>
                  <div className="price-item">
                    <span className="label">Confidence:</span>
                    <span className="value">{rec.confidence_score}%</span>
                  </div>
                </div>
                <div className="recommendation-reason">
                  <strong>Recommendation:</strong> {rec.recommendation_reason}
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Optimization Summary */}
      {Object.keys(optimizationSummary).length > 0 && (
        <div className="optimization-section">
          <h2>Optimization Summary</h2>
          <div className="optimization-content">
            {optimizationSummary.total_revenue_impact && (
              <div className="optimization-card">
                <h3>Revenue Impact</h3>
                <p className="optimization-value">₹{optimizationSummary.total_revenue_impact.toLocaleString()}</p>
              </div>
            )}
            {optimizationSummary.products_optimized && (
              <div className="optimization-card">
                <h3>Products Optimized</h3>
                <p className="optimization-value">{optimizationSummary.products_optimized}</p>
              </div>
            )}
            {optimizationSummary.average_improvement && (
              <div className="optimization-card">
                <h3>Average Improvement</h3>
                <p className="optimization-value">{optimizationSummary.average_improvement}%</p>
              </div>
            )}
            {optimizationSummary.recommendations_applied && (
              <div className="optimization-card">
                <h3>Recommendations Applied</h3>
                <p className="optimization-value">{optimizationSummary.recommendations_applied}</p>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default DynamicPricingDashboard;
