import React, { useState } from 'react';
import './App.css';
import HomePage from './components/HomePage';
import DemandForecastingDashboard from './components/DemandForecastingDashboard';
import DynamicPricingDashboard from './components/DynamicPricingDashboard';

function App() {
  const [currentPage, setCurrentPage] = useState('home');

  const handleNavigation = (page) => {
    setCurrentPage(page);
  };

  const renderPage = () => {
    switch (currentPage) {
      case 'demand-forecasting':
        return <DemandForecastingDashboard />;
      case 'dynamic-pricing':
        return <DynamicPricingDashboard />;
      case 'home':
      default:
        return <HomePage onNavigate={handleNavigation} />;
    }
  };

  return (
    <div className="App">
      {renderPage()}
    </div>
  );
}

export default App;
