import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import './DynamicPricingDashboard.css';

const DynamicPricingDashboard = () => {
  const [overviewData, setOverviewData] = useState({
    total_recommendations: 0,
    average_price_change: 0,
    average_confidence: 0,
    unique_products: 0
  });
  
  const [analyticsData, setAnalyticsData] = useState([]);
  const [optimizationSummary, setOptimizationSummary] = useState({});
  const [recommendations, setRecommendations] = useState([]);
  
  const [formData, setFormData] = useState({
    products: [
      {
        name: '',
        current_price: '',
        category: '',
        demand_score: '',
        competition_price: '',
        stock_level: '',
        days_since_last_update: ''
      }
    ]
  });
  
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Fetch overview data on component mount
  useEffect(() => {
    fetchOverviewData();
    fetchAnalyticsData();
    fetchOptimizationSummary();
  }, []);

  const fetchOverviewData = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/dashboard/overview');
      if (response.data.dynamic_pricing) {
        setOverviewData({
          total_recommendations: response.data.dynamic_pricing.total_recommendations || 0,
          average_price_change: response.data.dynamic_pricing.average_price_change || 0,
          average_confidence: response.data.dynamic_pricing.average_confidence || 0,
          unique_products: response.data.dynamic_pricing.unique_products || 0
        });
      }
    } catch (error) {
      console.error('Error fetching overview data:', error);
      setError('Failed to fetch overview data');
    }
  };

  const fetchAnalyticsData = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/pricing/analytics');
      // Ensure analytics data is always an array
      const data = response.data;
      if (Array.isArray(data)) {
        setAnalyticsData(data);
      } else if (data && Array.isArray(data.analytics)) {
        // Handle case where data is wrapped in an object
        setAnalyticsData(data.analytics);
      } else {
        console.warn('Analytics API response is not an array:', data);
        setAnalyticsData([]);
      }
    } catch (error) {
      console.error('Error fetching analytics data:', error);
      setError('Failed to fetch analytics data');
      setAnalyticsData([]); // Ensure empty array on error
    }
  };

  const fetchOptimizationSummary = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/pricing/optimization-summary');
      setOptimizationSummary(response.data || {});
    } catch (error) {
      console.error('Error fetching optimization summary:', error);
      setError('Failed to fetch optimization summary');
    }
  };

  const handleInputChange = (index, field, value) => {
    const updatedProducts = [...formData.products];
    updatedProducts[index][field] = value;
    setFormData({ products: updatedProducts });
  };

  const addProduct = () => {
    setFormData({
      products: [
        ...formData.products,
        {
          name: '',
          current_price: '',
          category: '',
          demand_score: '',
          competition_price: '',
          stock_level: '',
          days_since_last_update: ''
        }
      ]
    });
  };

  const removeProduct = (index) => {
    if (formData.products.length > 1) {
      const updatedProducts = formData.products.filter((_, i) => i !== index);
      setFormData({ products: updatedProducts });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    
    try {
      const response = await axios.post('http://localhost:5000/api/pricing/recommend', formData);
      setRecommendations(response.data.recommendations || []);
    } catch (error) {
      console.error('Error getting recommendations:', error);
      setError('Failed to get pricing recommendations');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="dashboard-container">
      <h1 className="dashboard-title">Dynamic Pricing Dashboard</h1>
      
      {error && <div className="error-message">{error}</div>}
      
      {/* Stats Cards */}
      <div className="stats-grid">
        <div className="stat-card">
          <h3>Total Recommendations</h3>
          <p className="stat-value">{overviewData.total_recommendations}</p>
        </div>
        <div className="stat-card">
          <h3>Average Price Change</h3>
          <p className="stat-value">{overviewData.average_price_change.toFixed(2)}%</p>
        </div>
        <div className="stat-card">
          <h3>Average Confidence</h3>
          <p className="stat-value">{overviewData.average_confidence.toFixed(2)}%</p>
        </div>
        <div className="stat-card">
          <h3>Unique Products</h3>
          <p className="stat-value">{overviewData.unique_products}</p>
        </div>
      </div>

      {/* Main Content Grid */}
      <div className="main-content">
        {/* Analytics Chart */}
        <div className="chart-section">
          <h2>Price Analytics</h2>
          <div className="chart-container">
            <ResponsiveContainer width="100%" height={400}>
              <LineChart data={analyticsData}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="category" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Line 
                  type="monotone" 
                  dataKey="average_current_price" 
                  stroke="#8884d8" 
                  strokeWidth={2}
                  name="Average Current Price"
                />
                <Line 
                  type="monotone" 
                  dataKey="average_recommended_price" 
                  stroke="#82ca9d" 
                  strokeWidth={2}
                  name="Average Recommended Price"
                />
              </LineChart>
            </ResponsiveContainer>
          </div>
        </div>

        {/* Input Form */}
        <div className="input-section">
          <h2>Get Pricing Recommendations</h2>
          <form onSubmit={handleSubmit} className="pricing-form">
            {formData.products.map((product, index) => (
              <div key={index} className="product-input-group">
                <h3>Product {index + 1}</h3>
                <div className="input-grid">
                  <div className="input-group">
                    <label>Product Name:</label>
                    <input
                      type="text"
                      value={product.name}
                      onChange={(e) => handleInputChange(index, 'name', e.target.value)}
                      required
                    />
                  </div>
                  <div className="input-group">
                    <label>Current Price:</label>
                    <input
                      type="number"
                      step="0.01"
                      value={product.current_price}
                      onChange={(e) => handleInputChange(index, 'current_price', e.target.value)}
                      required
                    />
                  </div>
                  <div className="input-group">
                    <label>Category:</label>
                    <input
                      type="text"
                      value={product.category}
                      onChange={(e) => handleInputChange(index, 'category', e.target.value)}
                      required
                    />
                  </div>
                  <div className="input-group">
                    <label>Demand Score (1-10):</label>
                    <input
                      type="number"
                      min="1"
                      max="10"
                      value={product.demand_score}
                      onChange={(e) => handleInputChange(index, 'demand_score', e.target.value)}
                      required
                    />
                  </div>
                  <div className="input-group">
                    <label>Competition Price:</label>
                    <input
                      type="number"
                      step="0.01"
                      value={product.competition_price}
                      onChange={(e) => handleInputChange(index, 'competition_price', e.target.value)}
                      required
                    />
                  </div>
                  <div className="input-group">
                    <label>Stock Level:</label>
                    <input
                      type="number"
                      value={product.stock_level}
                      onChange={(e) => handleInputChange(index, 'stock_level', e.target.value)}
                      required
                    />
                  </div>
                  <div className="input-group">
                    <label>Days Since Last Update:</label>
                    <input
                      type="number"
                      value={product.days_since_last_update}
                      onChange={(e) => handleInputChange(index, 'days_since_last_update', e.target.value)}
                      required
                    />
                  </div>
                </div>
                {formData.products.length > 1 && (
                  <button 
                    type="button" 
                    onClick={() => removeProduct(index)}
                    className="remove-btn"
                  >
                    Remove Product
                  </button>
                )}
              </div>
            ))}
            
            <div className="form-actions">
              <button type="button" onClick={addProduct} className="add-btn">
                Add Another Product
              </button>
              <button type="submit" disabled={loading} className="submit-btn">
                {loading ? 'Getting Recommendations...' : 'Get Recommendations'}
              </button>
            </div>
          </form>
        </div>
      </div>

      {/* Recommendations Results */}
      {recommendations.length > 0 && (
        <div className="results-section">
          <h2>Pricing Recommendations</h2>
          <div className="recommendations-grid">
            {recommendations.map((rec, index) => (
              <div key={index} className="recommendation-card">
                <h3>{rec.product_name}</h3>
                <div className="price-info">
                  <div className="price-item">
                    <span className="label">Current Price:</span>
                    <span className="value">₹{rec.current_price}</span>
                  </div>
                  <div className="price-item">
                    <span className="label">Recommended Price:</span>
                    <span className="value recommended">₹{rec.recommended_price}</span>
                  </div>
                  <div className="price-item">
                    <span className="label">Days Left:</span>
                    <span className="value">{rec.days_left}</span>
                  </div>
                  <div className="price-item">
                    <span className="label">Confidence:</span>
                    <span className="value">{rec.confidence_score}%</span>
                  </div>
                </div>
                <div className="recommendation-reason">
                  <strong>Recommendation:</strong> {rec.recommendation_reason}
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Optimization Summary */}
      {Object.keys(optimizationSummary).length > 0 && (
        <div className="optimization-section">
          <h2>Optimization Summary</h2>
          <div className="optimization-content">
            {optimizationSummary.total_revenue_impact && (
              <div className="optimization-card">
                <h3>Revenue Impact</h3>
                <p className="optimization-value">₹{optimizationSummary.total_revenue_impact.toLocaleString()}</p>
              </div>
            )}
            {optimizationSummary.products_optimized && (
              <div className="optimization-card">
                <h3>Products Optimized</h3>
                <p className="optimization-value">{optimizationSummary.products_optimized}</p>
              </div>
            )}
            {optimizationSummary.average_improvement && (
              <div className="optimization-card">
                <h3>Average Improvement</h3>
                <p className="optimization-value">{optimizationSummary.average_improvement}%</p>
              </div>
            )}
            {optimizationSummary.recommendations_applied && (
              <div className="optimization-card">
                <h3>Recommendations Applied</h3>
                <p className="optimization-value">{optimizationSummary.recommendations_applied}</p>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default DynamicPricingDashboard;
